import React, { Component } from 'react';
import './Field.css'
import { player_state, setCardPlayedDisplayCallback } from '../gameState/gameState';

class Field extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tower_url: '../assets/',
            tower1: 0,
            tower2: 0,
            tower3: 0,
            tower4: 0,
        }
    }

    render() {
        return (
            <div className="fixed-field">
                <ul className="playing-field">
                    {Object.keys(player_state).map(function(name, index){
                        return <div><img className="tower-img" src={require(this.state.url + index)}/></div>
                    })}
                    <div><img className="tower-img" src={require('../assets/0.png')}/></div>
                </ul>
            </div>
        );
    }
}

export default Field;
