import React, { Component } from 'react';
import './Field.css'
import { player_state, setCardPlayedDisplayCallback } from '../gameState/gameState';

class Field extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tower0: null,
            tower1: null,
            tower2: null,
            tower3: null,
        }
    }
    render() {
        return (
            <div className="fixed-field">
                <ul className="playing-field">
                    {Object.values(player_state).map(function(player, index){

                        // Setting State
                        let tower_state = 'tower' + index;
                        this.state.tower_state = player.tower.health;
                        console.log(this.state.tower_state);
                        // Displaying Tower
                        return <div key={index}><img 
                            className="tower-img" 
                            id={player} 
                            src={require('../assets/' + player.tower.health + '.png')}/>
                        </div>
                    })}
                </ul>
            </div>
        );
    }
}

export default Field;
