import React, { Component } from 'react';
import './Field.css'
import { player_state, setCardPlayedDisplayCallback } from '../gameState/gameState';

class Field extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tower1: 0,
            tower2: 0,
            tower3: 0,
            tower4: 0,
        }
    }

    getTowers() {
        for (tower in player_state.tower) {
            console.log(player_state.tower.health);
        }
    }

    render() {
        return (
            <div className="fixed-field">
                <ul className="playing-field">
                    {Object.entries(player_state).map(function(player, index){
                        return <div><img className="tower-img" src={require('../assets/' + index + '.png')}/></div>
                    })}
                </ul>
            </div>
        );
    }
}

export default Field;
